def contiennumero(arrayjournalistes)
	g = arrayjournalistes.select { |i| i =~ /[0123456789]/ }   #"regex" ; fonctionne aussi    avec  =~ /[0-9]/
	print "#{g} \n"
	puts g.class
	puts g.length

end
#q2(arrayjournalistes)

def contienaude(arrayjournalistes)			#on a essayer plein de truc avec ça mais c la merde
	puts "il y a #{arrayjournalistes.count{ |i| i.include?("Aude".downcase) }} \"Aude\" sans la majuscule"
	puts "il y a #{arrayjournalistes.count{ |i| i.include? "Aude"}} \"Aude\" avec la majuscule"
end
