def orderbylength(arrayjournalistes)
	g = arrayjournalistes.sort_by {|x| x.length}
	print g
	retourligne
end

def selec50stshorter(arrayjournalistes)
	g = arrayjournalistes.sort_by {|x| x.length}
	g = g.slice(0...50)
	puts g.length
	print g
	retourligne
end


def positionepenser(arrayjournalistes)
	puts arrayjournalistes.index("@epenser")
end

